<style>
.cancellation-procedure {
    position: relative;
}
.cancellation-procedure:before {
    content: "";
    background: url(/wp-content/themes/fpalaw/assets/img/mfc/procedure-bg.png) center center no-repeat;
    background-size: cover;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
}
.cancellation-procedure h2.heading {
    font-family: 'Inter';
    font-style: normal;
    font-weight: 700;
    font-size: 34px;
    line-height: 145%;
    text-align: center;
    color: #FFFFFF;
    margin-bottom: 25px;
}
.cancellation-procedure p.desc {
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 18px;
    line-height: 145%;
    text-align: center;
    color: #FFFFFF;
    margin-bottom: 45px;
}
.cancellation-procedure .content {
    padding: 140px 0;
}
.procedure-steps .item {
    display: flex;
    justify-content: center;
}
.procedure-steps .item+.item {
    margin-top: 45px;
}
.procedure-steps .item .col {
    margin-right: 15px;
    margin-left: 15px;
}
.procedure-steps .item .col.number {
    max-width: 200px;
    width: 100%;
}
.procedure-steps .item .col.text {
    max-width: 420px;
    width: 100%;
}
.procedure-steps .item .col.text .title {
    font-style: normal;
    font-weight: 700;
    font-size: 24px;
    line-height: 145%;
    color: #FFFFFF;
    margin-bottom: 10px;
}
.procedure-steps .item .col.text .desription {
    font-family: 'Inter';
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 145%;
    color: #FFFFFF;
}
@media (max-width: 1080px) {
    .cancellation-procedure .content {
        padding: 100px 0;
    }
}
@media (max-width: 782px) {
    .cancellation-procedure .content {
        padding: 60px 0;
    }
}
@media (max-width: 440px) {
    .cancellation-procedure .content {
        padding: 40px 0;
    }
    .cancellation-procedure h2.heading {
        font-size: 22px;
        margin-bottom: 10px;
    }
    .cancellation-procedure p.desc {
        font-size: 16px;
        margin-bottom: 35px;
    }
    .procedure-steps .item .col.text .title {
        font-size: 16px;
        margin-bottom: 10px;
    }
    .procedure-steps .item .col.text .desription {
        font-size: 14px;
    }
    .procedure-steps .item+.item {
        margin-top: 35px;
    }
    .procedure-steps .item .col {
        margin-right: 0px;
        margin-left: 0px;
    }
    .procedure-steps .item .col+.col {
        margin-left: 10px;
    }
    .procedure-steps .item .col.number {
        max-width: 80px;
        width: 100%;
    }
    .procedure-steps .item .col.number svg {
        width: 100%;
        height: auto;
    }
    .procedure-steps .item .col.text {
        max-width: calc(100% - 100px);
        width: 100%;
    }
}
@media (max-width: 380px) {
    .procedure-steps .item .col.number {
        max-width: 60px;
        width: 100%;
    }
    .procedure-steps .item .col.text {
        max-width: calc(100% - 80px);
        width: 100%;
    }
    .procedure-steps .item .col.text .desription {
        font-size: 12px;
    }
}








</style>
